const Section2 = () => {


    //Grab bar event listeners
    const d = document.getElementsByClassName("big_circle");

    for (let i = 0; i < d.length; i++) {
    d[i].style.position = "relative";
    }

function filter(e) {
  let target = e.target;

  if (!target.classList.contains("big_circle")) {
    return;
  }

  target.moving = true;

  //NOTICE THIS ðŸ‘‡ Check if Mouse events exist on users' device
  if (e.clientX) {
    target.oldX = e.clientX; // If they exist then use Mouse input
    // target.oldY = e.clientY;
  } else {
    target.oldX = e.touches[0].clientX; // Otherwise use touch input
    //target.oldY = e.touches[0].clientY;
  }
  //NOTICE THIS ðŸ‘† Since there can be multiple touches, you need to mention which touch to look for, we are using the first touch only in this case

  target.oldLeft = window.getComputedStyle(target).getPropertyValue('left').split('px')[0] * 1;
  target.oldTop = window.getComputedStyle(target).getPropertyValue('top').split('px')[0] * 1;

  document.onmousemove = dr;
  //NOTICE THIS ðŸ‘‡
  document.ontouchmove = dr;
  //NOTICE THIS ðŸ‘†

  function dr(event) {
    event.preventDefault();

    if (!target.moving) {
      return;
    }
    //NOTICE THIS ðŸ‘‡
    if (event.clientX) {
      target.distX = (event.clientX >target.oldX) ? event.clientX - target.oldX : 0;
      //target.distY = event.clientY - target.oldY;
    } else {
      target.distX = (event.touches[0].clientX - target.oldX) ? event.touches[0].clientX - target.oldX : 0;
      //target.distY = event.touches[0].clientY - target.oldY;
    }
    //NOTICE THIS ðŸ‘†

    target.style.left = (target.oldLeft + target.distX>0) ? target.oldLeft + target.distX + "px":0;
    //target.style.top = target.oldTop + target.distY + "px";
  }

  function endDrag() {
    target.moving = false;
  }
  target.onmouseup = endDrag;
  //NOTICE THIS ðŸ‘‡
  target.ontouchend = endDrag;
  //NOTICE THIS ðŸ‘†
}
document.onmousedown = filter;
//NOTICE THIS ðŸ‘‡
document.ontouchstart = filter;
