import './Section2.css';
import {useState} from 'react';

const Section2 = () => {


    //Grab bar event listeners
    const [toggled, setToggled] = useState(false);
    const ligne = document.getElementsByClassName('chronoligne');

    const toggle = () => {
        if(toggled){
            ligne.style.transform = 'translateX(0%)';
            setToggled(false);
        }else{
            setToggled(true);
        }
    };
    

    return(
        <div id="section2">
            <h1 className="titre">Education</h1>
            <h1 className="titre ombre3">Education</h1>
            <div className="chronoligne">
                <div className="ligne"></div>
                <div className="big_circle" onClick={toggle}></div>
           
            </div>
            <div className="chrono_container">
                <div className="card c1">
                    <h2>Prep School</h2>
                </div>
                <div className="card c2">
                    <h2>CentraleSup√©lec</h2>
                </div>
                <div className="card c3">
                    <h2>IELTS</h2>
                </div>
            </div>
        </div>
    )
}

export default Section2;
