import './Section2.css';

const Section2 = () => {


    //Grab bar event listeners
    var mousePosition;
    var offset = [0,0];
    var isDown = false;

    var div = document.getElementsByClassName('big_circle');

    div.addEventListener('mousedown', function(e) {
        isDown = true;
        offset = [
            div.offsetLeft - e.clientX,
            div.offsetTop - e.clientY
        ];
    }, true);
    
    document.addEventListener('mouseup', function() {
        isDown = false;
    }, true);

    document.addEventListener('mousemove', function(event) {
        event.preventDefault();
        if (isDown) {
            mousePosition = {
    
                x : event.clientX,
                y : event.clientY
    
            };
            div.style.left = (mousePosition.x + offset[0]) + 'px';
            div.style.top  = (mousePosition.y + offset[1]) + 'px';
        }
    }, true);


    return(
        <div id="section2">
            <h1 className="titre">Education</h1>
            <h1 className="titre ombre3">Education</h1>
            <div className="chronoligne">
                <div className="ligne"></div>
                <div className="big_circle"></div>
           
            </div>
            <div className="chrono_container">
                <div className="card c1">
                    <h2>Prep School</h2>
                </div>
                <div className="card c2">
                    <h2>CentraleSup√©lec</h2>
                </div>
                <div className="card c3">
                    <h2>IELTS</h2>
                </div>
            </div>
        </div>
    )
}

export default Section2;
