import './Section2.css';
import {useState} from 'react';

const Section2 = () => {

    
    //Grab bar event listeners
    const d = document.getElementsByClassName("big_circle");
    const largeur = window.innerWidth;
    const marge = largeur/10;

    for (let i = 0; i < d.length; i++) {
      d[i].style.position = "relative";
    }
    
    function filter(e) {
      let target = e.target;
    
      if (!target.classList.contains("big_circle")) {
        return;
      }
    
      target.moving = true;
    
      //NOTICE THIS ðŸ‘‡ Check if Mouse events exist on users' device
      if (e.clientX) {
        target.oldX = e.clientX; // If they exist then use Mouse input 
      } else if(e.touches){    
        target.oldX = e.touches[0].clientX; // Otherwise use touch input
      }
      //NOTICE THIS ðŸ‘† Since there can be multiple touches, you need to mention which touch to look for, we are using the first touch only in this case
    
      target.oldLeft = window.getComputedStyle(target).getPropertyValue('left').split('px')[0] * 1;

    
      document.onmousemove = dr;
      //NOTICE THIS ðŸ‘‡
      document.ontouchmove = dr;
      //NOTICE THIS ðŸ‘†
    
      function dr(event) {
        event.preventDefault();
    
        if (!target.moving) {
          return;
        }
        //NOTICE THIS ðŸ‘‡
        if (event.clientX) {

          target.distX = (event.clientX>marge && event.clientX<largeur -marge) ? event.clientX - target.oldX : target.distX;
        } else if(event.touches){
          target.distX =event.touches[0].clientX - target.oldX;
        }
        //NOTICE THIS ðŸ‘†
    
        target.style.left = target.oldLeft + target.distX + "px";
      }
    
      function endDrag() {
        target.moving = false;
      }
      target.onmouseup = endDrag;
      //NOTICE THIS ðŸ‘‡
      target.ontouchend = endDrag;
      //NOTICE THIS ðŸ‘†
    }
    document.onmousedown = filter;
    //NOTICE THIS ðŸ‘‡
    document.ontouchstart = filter;
    //NOTICE THIS ðŸ‘†
    

    return(
        <div id="section2">
            <h1 className="titre">Education</h1>
            <h1 className="titre ombre3">Education</h1>
            <div className="chronoligne">
                <div className="ligne"></div>
                <div className="big_circle"></div>
           
            </div>
            <div className="chrono_container">
                <div className="card c1">
                    <h2>Jules Ferry's Preparation School</h2>
                    <ul>
                      <li><p><strong>Description :</strong>This was a 2 years intensive and (hard) school in order to prepare for the competitive 
                        exams to have the opportunity to enter the best french schools of engineering. 
                      </p>
                      </li>
                      <li><strong>Place : </strong>Versailles, France</li>
                    </ul>
                </div>
                <div className="card c2">
                    <h2>CentraleSupÃ©lec</h2>
                </div>
                <div className="card c3">
                    <h2>IELTS</h2>
                    <ul>
                      <li><p><strong>Description :</strong> The IELTS is an International English Language Testing System</p></li>
                      <li><p><strong>Date :</strong> December 2021.</p></li>
                      <li><p><strong>Grade :</strong> 7 out of the 9 (C1 level).</p></li>
                    </ul>
                    

                </div>
            </div>
        </div>
    )
}

export default Section2;
